import 'package:flutter/material.dart';

class StudiKasus03 extends StatefulWidget {
  const StudiKasus03({super.key});

  @override
  // ignore: library_private_types_in_public_api
  _StudiKasus03State createState() => _StudiKasus03State();
}

class _StudiKasus03State extends State<StudiKasus03> {
  String? selectedHokage;
  String imageUrl = ''; // Untuk menyimpan URL gambar

  // Data gambar untuk setiap Hokage
  final Map<String, String> hokageImages = {
    'Hokage 1':
        'data:image/jpeg;base64,',
    'Hokage 2':
        'data:image/jpeg;base64,',
    'Hokage 3':
        'data:image/jpeg;base64,',
    'Hokage 4':
        'data:image/jpeg;base64,',
    'Hokage 5':
        'https://assets.pikiran-rakyat.com/crop/0x0:0x0/750x500/photo/2022/10/23/934786489.jpg',
  };

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Studi Kasus 03: Dropdown dan Load Gambar'),
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            // Dropdown Button untuk memilih Hokage
            const Text('Pilih Hokage:'),
            DropdownButton<String>(
              value: selectedHokage,
              hint: const Text('Pilih Hokage'),
              onChanged: (String? newValue) {
                setState(() {
                  selectedHokage = newValue;
                });
              },
              items: hokageImages.keys.map((String hokage) {
                return DropdownMenuItem<String>(
                  value: hokage,
                  child: Text(hokage),
                );
              }).toList(),
            ),
            const SizedBox(height: 20),
            // Elevated Button untuk memuat gambar
            ElevatedButton(
              onPressed: () {
                if (selectedHokage != null) {
                  setState(() {
                    imageUrl = hokageImages[selectedHokage!]!;
                  });
                }
              },
              child: const Text('Tampilkan Gambar Hokage'),
            ),
            const SizedBox(height: 20),
            // Menampilkan gambar berdasarkan pilihan
            if (imageUrl.isNotEmpty)
              Image.network(
                imageUrl,
                height: 250.0,
                width: double.infinity,
                fit: BoxFit.cover,
                loadingBuilder: (context, child, loadingProgress) {
                  if (loadingProgress == null) return child;
                  return Center(
                    child: CircularProgressIndicator(
                      value: loadingProgress.expectedTotalBytes != null
                          ? loadingProgress.cumulativeBytesLoaded /
                              (loadingProgress.expectedTotalBytes ?? 1)
                          : null,
                    ),
                  );
                },
                errorBuilder: (context, error, stackTrace) {
                  return const Center(
                    child: Text('Gagal memuat gambar'),
                  );
                },
              ),
          ],
        ),
      ),
    );
  }
}
